3.watcher类
class Watcher{
    constructor(el, vm, exp, attr){
        this.el = el;
        this.vm = vm;
        this.exp = exp;
        this.attr = attr;
        this.update();
    }
    update(){
        this.el[this.attr] = this.vm.$data[this.exp];
    };
}
